<template>  <div class="nav-header">    <div class="content">      <p>局部仿真数据可视化平台</p>    </div>    <div class="time">      <p>{{ dataTime }}</p>    </div>  </div></template><script lang="ts">import { defineComponent, Ref, ref, onMounted, onBeforeMount } from 'vue'export default defineComponent({  setup() {    let dataTime = ref('')    //定义 timer 初始值及类型    let timer: Ref<any> = ref(null)    // 当前时间    const getNowTime = () => {      const now = new Date()      const year = now.getFullYear()      const month = now.getMonth() >= 9 ? now.getMonth() + 1 : `0${now.getMonth() + 1}`      const date = now.getDate() >= 10 ? now.getDate() : `0${now.getDate()}`      const hour = now.getHours() >= 10 ? now.getHours() : `0${now.getHours()}`      const minutes = now.getMinutes() >= 10 ? now.getMinutes() : `0${now.getMinutes()}`      const seconds = now.getSeconds() >= 10 ? now.getSeconds() : `0${now.getSeconds()}`      dataTime.value = `${year}年${month}月${date}日    ${hour}:${minutes}:${seconds}`    }    onMounted(() => {      getNowTime()      timer.value = setInterval(() => {        getNowTime()      }, 1000)    })    onBeforeMount(() => {      // 清理定时器要处理 timer 的类型      clearInterval(Number(timer))    })    return {      dataTime    }  }})</script><style scoped lang="less">.nav-header {  position: relative;  width: 960px;  height: 130px;  background-image: radial-gradient(    circle farthest-side at top,    rgba(26, 80, 75, 1),    rgba(26, 80, 75, 0.1)  );  .content {    position: relative;    display: flex;    align-items: center;    background-image: url('~@/assets/images/header_bg_960.png');    background-size: 960px 88px;    width: 100%;    height: 88px;    opacity: 0.9;    margin: 0 auto;    p {      position: absolute;      color: #dfece1;      text-align: center;      font-size: 36px;      font-weight: bolder;      font-font: 'siyuan';      left: 50%;      transform: translateX(-50%);    }  }  .time {    position: relative;    align-items: center;    display: flex;    height: 42px;    width: 550px;    left: 50%;    transform: translateX(-50%);    &::before {      position: absolute;      content: '';      width: 30px;      height: 10px;      border-top: 3px solid rgb(55, 237, 215);      border-left: 3px solid rgb(55, 237, 215);      top: 10px;      left: 100px;    }    &::after {      position: absolute;      content: '';      width: 30px;      height: 10px;      border-bottom: 3px solid rgb(55, 237, 215);      border-right: 3px solid rgb(55, 237, 215);      bottom: 10px;      right: 100px;    }    p {      position: absolute;      color: #dfece1;      text-align: center;      font-family: 'DS-DIGIT';      font-size: 24px;      font-weight: bold;      left: 50%;      transform: translateX(-50%);    }  }}</style>